mapper.select:
SELECT COUNT(*)
FROM REVIEW
WHERE USER_NO = #{userNo} AND MERCHANT_UID = #{merchantUid}

mapper.update:
UPDATE REVIEW
SET REVIEW_TITLE = #{reviewTitle},
    REVIEW_CONTENT = #{reviewContent}
WHERE USER_NO = #{userNo} AND MERCHANT_UID=#{merchantUid}

mapper.select:
SELECT MERCHANT_UID
FROM REVIEW
WHERE USER_NO = #{userNo}

mapper.update:
UPDATE REVIEW
	SET REVIEW_STATUS = 'Y'
WHERE MERCHANT_UID = #{merchantUid} AND USER_NO = #{userNo}
