mapper.select:
SELECT COUNT(*)
FROM USER_INFO
WHERE EMAIL = #{email}

SELECT COUNT(*)
FROM USER_INFO
WHERE PHONE = #{phone}

SELECT COUNT(*)
FROM USER_INFO
WHERE NAME = #{name}

mapper.update:
UPDATE USER_INFO
SET USER_PW = #{encodedPassword}
WHERE USER_ID = #{userId} AND NAME = #{name} AND USER_STATUS = 'N' AND (EMAIL = #{authMethod} OR PHONE = #{authMethod})

mapper.select:
SELECT COUNT(*)
FROM USER_INFO
WHERE METHOD = #{signupMethod} AND EMAIL = #{email}
